package com.constructor;

public class P7 {
	int i;
	P7()
	{
		i=10;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		P7 p1=new P7();
		P7 p2=new P7();
		P7 p3=new P7();
		
		System.out.println("i value for my object 1:"+p1.i);
		System.out.println("i value for my object 1:"+p2.i);
		System.out.println("i value for my object 1:"+p3.i);
	}

}
